import React, { Component } from 'react';
import './App.css';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import TaskControl from './components/TaskControl';
// import _ from 'lodash';
import { connect } from 'react-redux';
import * as actions from './actions/index';
class App extends Component {
    constructor (props) {
        super(props);
        this.state = {
            filter        : {
                name   : '',
                status : -1
            },
            keyword   : '',
            sortBy    : 'name',
            sortValue :  1
        }
    }

    onGenerateData = () => {
        var tasks = [
            {
                id     : this.generateID(),
                name   : 'Hoc lap trinh',
                status : true
            },
            {
                id     : this.generateID(),
                name   : 'Di boi',
                status : false
            },
            {
                id     : this.generateID(),
                name   : 'Ngu',
                status : true
            }
        ];

        localStorage.setItem('tasks', JSON.stringify(tasks));

    }

    onToggleForm = () => {
        var { itemEditing } = this.props;
        if (itemEditing && itemEditing.id !== '') {
            this.props.onOpenForm();
        } else {
            this.props.onToggleForm();
        }
        this.props.onClearTask({
            id : '',
            name : '',
            status : false
        });
    }

    onShowForm = () => {
        this.setState({
            isDisplayFrom: true
        })
    }

    //filter
    onFilter = (filterName , filterStatus) => {
        filterStatus = parseInt(filterStatus,10);

        this.setState({
            filter : {
                name    : filterName.toLowerCase(),
                status  : filterStatus
            }
        });
    }
    //sreach
    onSreach = (keyword) => {
        this.setState({
            keyword :keyword
        });
    }

    onSort = (sortBy, sortValue) => {
            this.setState({
               sortBy: sortBy,
               sortValue: sortValue
            });
    }

    //find id
    findIndex = (id) => {
        var {tasks} = this.state;
        var result = -1;
        tasks.forEach((task, index) => {
            if(task.id === id){
                result = index;
            }
        });
        return result;
    }

    render() {
        var {
        filter,
        keyword,
        sortBy,
        sortValue } = this.state; //var tasks = this.state.tasks
        var { isDisplayFrom } = this.props;
        if(filter){
            if(filter.name){


            }
        }

        if(keyword){
            // tasks = tasks.filter((task) => {
            //     return task.name.toLowerCase().indexOf(keyword) !== -1;
            // });

        }

        if (sortBy === 'name'){

        } else{

        }
        return (
            <div className="container">
                <div className="text-center">
                    <h1>Quản Lý Công Việc</h1><hr/>
                </div>
                <div className="row">
                    <div className={ isDisplayFrom ? 'col-xs-4 col-sm-4 col-md-4 col-lg-4' : '' }>
                    <TaskForm
                    />
                    </div>
                    <div className= {isDisplayFrom ? 'col-xs-8 col-sm-8 col-md-8 col-lg-8' : 'col-xs-12 col-sm-12 col-md-12 col-lg-12' }>
                        <button
                        type="button"
                        className="btn btn-primary"
                        onClick={this.onToggleForm} >
                            <span className="fa fa-plus mr-5"></span>
                            Thêm Công Việc
                        </button>
                        <button
                        type="button"
                        className="btn btn-danger ml-5"
                        onClick={this.onGenerateData}>
                            <span className="fa fa-plus mr-5"></span>
                            Generate Data
                        </button>

                        <TaskControl
                        onSreach   = {this.onSreach}
                        onSort     = {this.onSort}
                        sortBy     = {sortBy}
                        sortValue  = {sortValue}
                         />


                        <TaskList
                        // onUpdate = { this.onUpdate }
                        onFilter = { this.onFilter } />
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        isDisplayFrom : state.isDisplayForm,
    };
}

const mapDispatchToProps = (dispatch, props) =>{
    return {
        onToggleForm : () => {
            dispatch(actions.toggleForm());
        },
        onClearTask : (task) =>{
            dispatch(actions.editTask(task));
        },
        onOpenForm : () => {
            dispatch(actions.openForm());
        }
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
